mapper.select:
select
p_no,c_no,p_name,price,delivery_fee,p_commnet,p_filename1,p_filename2,p_filename3,p_filename4,p_filename5,c_name,p_deltype,day_price
from product left join category using(c_no) where p_no=${param1}

select * from ( select * from ( select p_no,c_no,p_name,price,delivery_fee,p_commnet,p_filename1,p_filename2,p_filename3,p_filename4,p_filename5,to_char(p_date,'YYYY-MM-DD') p_date,c_name,p_deltype,day_price from product left join category using(c_no)
AND p_deltype = #{p_deltype}
AND ${searchKey} like '%'||#{searchWord}||'%'
AND c_no = ${c_no}
AND to_char(p_date,'YYYY-MM-DD') >TO_DATE('${s_date}','YYYY-MM-DD') and to_char(p_date,'YYYY-MM-DD') <= to_date('${e_date}','YYYY-MM-DD')
order by p_no desc) where rownum <= #{pageNum} * #{onePageRecord} order by p_no asc) where rownum <= #{lastPageRecord} #{onePageRecord} order by p_no desc

select * from ( select * from ( select rownum,s_no,p_no,p_name, to_char(s_regdate,'YYYY-MM-DD') s_regdate,s_deltype,note from product_stock left join product using(p_no)
p_no = ${p_no}
AND s_deltype = #{s_deltype}
order by p_no desc) where rownum <= #{pageNum} * #{onePageRecord} order by s_no asc) where rownum <= #{lastPageRecord} #{onePageRecord} order by s_no desc

select count(p_no) from product left join category using(c_no)
AND p_deltype = #{p_deltype}
AND ${searchKey} like '%'||#{searchWord}||'%'
AND c_no = ${c_no}
AND to_char(p_date,'YYYY-MM-DD') >to_date('${s_date}','YYYY-MM-DD') and to_char(p_date,'YYYY-MM-DD') <= to_date('${e_date}','YYYY-MM-DD')

select
count(p_no) from product_stock

	p_no = ${p_no}

		AND s_deltype = #{s_deltype}
